import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const BiosecurityChecklist = () => {
  const [checklist, setChecklist] = useState([
    { id: 1, item: 'Footbath at farm entrance', checked: false, critical: true },
    { id: 2, item: 'Visitor log maintained', checked: false, critical: true },
    { id: 3, item: 'Animals isolated when sick', checked: false, critical: true },
    { id: 4, item: 'Feed storage properly secured', checked: false, critical: false },
    { id: 5, item: 'Water sources protected', checked: false, critical: true },
    { id: 6, item: 'Waste disposal system in place', checked: false, critical: true },
    { id: 7, item: 'Regular cleaning schedule followed', checked: false, critical: false },
    { id: 8, item: 'Vaccination records updated', checked: false, critical: true },
    { id: 9, item: 'Equipment disinfected regularly', checked: false, critical: false },
    { id: 10, item: 'Dead animal disposal protocol', checked: false, critical: true }
  ]);

  const handleCheckChange = (id) => {
    setChecklist(prev => 
      prev.map(item => 
        item.id === id ? { ...item, checked: !item.checked } : item
      )
    );
  };

  const calculateScore = () => {
    const totalItems = checklist.length;
    const checkedItems = checklist.filter(item => item.checked).length;
    return Math.round((checkedItems / totalItems) * 100);
  };

  const criticalIssues = checklist.filter(item => item.critical && !item.checked);

  return (
    <div className="checklist-container">
      <header className="checklist-header">
        <Link to="/dashboard" className="back-btn">‚Üê Back to Dashboard</Link>
        <h2>Biosecurity Checklist</h2>
      </header>

      <div className="score-section">
        <div className="current-score">
          <h3>Current Score: {calculateScore()}%</h3>
          <div className="score-indicator">
            <div 
              className={`score-circle ${calculateScore() >= 80 ? 'good' : calculateScore() >= 60 ? 'warning' : 'danger'}`}
            >
              {calculateScore()}%
            </div>
          </div>
        </div>
      </div>

      {criticalIssues.length > 0 && (
        <div className="critical-alerts">
          <h4>‚ö†Ô∏è Critical Issues ({criticalIssues.length})</h4>
          <ul>
            {criticalIssues.map(issue => (
              <li key={issue.id}>{issue.item}</li>
            ))}
          </ul>
        </div>
      )}

      <div className="checklist-items">
        {checklist.map(item => (
          <div key={item.id} className={`checklist-item ${item.critical ? 'critical' : ''}`}>
            <label className="checkbox-container">
              <input
                type="checkbox"
                checked={item.checked}
                onChange={() => handleCheckChange(item.id)}
              />
              <span className="checkmark"></span>
              <span className="item-text">
                {item.item}
                {item.critical && <span className="critical-badge">Critical</span>}
              </span>
            </label>
          </div>
        ))}
      </div>

      <div className="action-buttons">
        <button className="btn-primary">Save Progress</button>
        <button className="btn-secondary">Generate Report</button>
      </div>
    </div>
  );
};

export default BiosecurityChecklist;
